import styled from 'styled-components';
import gql from "graphql-tag";
import { useQuery } from '@apollo/client';
import DisplayError from "./ErrorMessage";
import Head from 'next/head';
import SingleItemStyles from './styles/SingleItemStyles';
import ReactImageMagnify from 'react-image-magnify';
import Card from 'react-bootstrap/Card'
import {Container, Row, Col, Carousel} from 'react-bootstrap';

const Center = styled.div`
	text-align: center;
`;

const ItemsList = styled.div`
	display: grid;
	grid-template-columns: auto auto auto auto;
	grid-gap: 60px;
	max-width: ${props => props.theme.maxWidth};
	margin: 0 auto;
	@media (max-width: 830px) {
		grid-template-columns: 1fr 1fr;
		grid-gap: 0%;
		a {
    		font-size: 1.2rem;
		}
	}
	@media (max-width: 500px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;
	}
`;

const SINGLE_ITEM_QUERY = gql`
    query SINGLE_ITEM_QUERY ($id: ID!){
        Product(where: { id: $id }) {
            name
            price
            description
            id
            photo {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo2 {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo3 {
                altText
                image {
                    publicUrlTransformed
                }
            }

            photo4 {
                altText
                image {
                    publicUrlTransformed
                }
            }
        }
        
        # allProducts (where: {
        #   name_contains_i: "Yeti"
        # }) {
        #   name
        #   price
        #   description
        # }
    }

`;

export default function SingleProduct({ id }) {
    const { data, loading, error } = useQuery
        (SINGLE_ITEM_QUERY, {
            variables: {
                id,
            },
        });
    if (loading) return <p>Loading...</p>;
    if (error) return <DisplayError error={error} />; 
    const { Product } = data;
    console.log(Product)
    
    return (
        <SingleItemStyles>
            <Head>
                <title>Stax Of Stories - {Product.name}</title>
            </Head>
            <div className="item-details">
            
                {/* <ReactSlick
                    {...{
                        dots: true,
                        infinite: true,
                        speed: 500,
                        slidesToShow: 1,
                        slidesToScroll: 1
                    }}
                > */}
            
                {/* {!Product.photo && (
                    null
                )} */}
                
                {Product.photo && (
                    <ReactImageMagnify {...{
                        smallImage: {
                            alt: Product.photo.altText,
                            isFluidWidth: true,
                            src: Product.photo.image.publicUrlTransformed,
                            sizes: '(max-width: 480px) 100vw, (max-width: 1200px) 30vw, 360px'
                        },
                        largeImage: {
                            src: Product.photo.image.publicUrlTransformed,
                            width: 1426,
                            height: 2000
                        },
                        lensStyle: { backgroundColor: 'rgba(0,0,0,.6)' }
                    }} />
                )}

                {/* {!Product.photo2 && (
                    null
                )}

                {Product.photo2 && (
                    <ReactImageMagnify {...{
                        smallImage: {
                            alt: Product.photo2.altText,
                            isFluidWidth: true,
                            src: Product.photo2.image.publicUrlTransformed,
                            sizes: '(max-width: 480px) 100vw, (max-width: 1200px) 30vw, 360px'
                        },
                        largeImage: {
                            src: Product.photo2.image.publicUrlTransformed,
                            width: 1200,
                            height: 1800
                        },
                        lensStyle: { backgroundColor: 'rgba(0,0,0,.6)' }
                    }} />
                )}

                {!Product.photo3 && (
                    null
                )}

                {Product.photo3 && (
                    <ReactImageMagnify {...{
                        smallImage: {
                            alt: Product.photo3.altText,
                            isFluidWidth: true,
                            src: Product.photo3.image.publicUrlTransformed,
                            sizes: '(max-width: 480px) 100vw, (max-width: 1200px) 30vw, 360px'
                        },
                        largeImage: {
                            src: Product.photo3.image.publicUrlTransformed,
                            width: 1200,
                            height: 1800
                        },
                        lensStyle: { backgroundColor: 'rgba(0,0,0,.6)' }
                    }} />
                )}

                {!Product.photo4 && (
                    null
                )}

                {Product.photo4 && (
                    <ReactImageMagnify {...{
                        smallImage: {
                            alt: Product.photo4.altText,
                            isFluidWidth: true,
                            src: Product.photo4.image.publicUrlTransformed,
                            sizes: '(max-width: 480px) 100vw, (max-width: 1200px) 30vw, 360px'
                        },
                        largeImage: {
                            src: Product.photo4.image.publicUrlTransformed,
                            width: 1200,
                            height: 1800
                        },
                        lensStyle: { backgroundColor: 'rgba(0,0,0,.6)' }
                    }} /> 
                )}  */}

                {/* </ReactSlick> */}

                <div className="details">
                    <h2>{Product.name}</h2>
                    <p>{Product.description}</p>
                </div>
            </div>
        </SingleItemStyles>
    );
}
