import Form from './styles/Form';
import gql from 'graphql-tag';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import useForm from '../lib/useForm';
import DisplayError from './ErrorMessage';
import { 
    faEye,
} from '@fortawesome/free-solid-svg-icons';

const EyeReveal = styled.div`
  .cursor-pointer {
    cursor: pointer;
  }

  .show{
  	opacity: 1;
  }

  .hide {
  	opacity: 0.5;
  }
`

export default function SignIn() {

    const { inputs, handleChange, resetForm } = useForm({
        email: '',
        password: '',
    });

    // const [createProduct, { data, error, loading }] = useMutation(
    //     CREATE_PRODUCT_MUTATION, 
    //     {
    //         variables: inputs,
    //         refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
    //     }
    // );

    return (
        <Form method="POST">
            <fieldset>
                <h2>Sign Into Your Account</h2>
                {/* <DisplayError error={error} /> */}
                <label htmlFor="email">
                    Email
                    <input 
                        type="email" 
                        name="email" 
                        placeholder="Your Email Address" 
                        autoComplete="email"
                        value={inputs.email}
                        onChange={handleChange}
                    />
                </label>
                <EyeReveal>
                    <label htmlFor="password">
                        Password
                            
                            <div className="float-right mt-1 cursor-pointer">
                                <small>
                                    <span className="pr-1">Show Password </span>
                                    <FontAwesomeIcon icon={faEye} />
                                </small>
                            </div>
                                                            
                        <input 
                            type="password"
                            name="password" 
                            placeholder="Password" 
                            autoComplete="paasword"
                            value={inputs.password}
                            onChange={handleChange}
                        />
                        
                    </label>
                </EyeReveal>
                <button type="submit">Sign In!</button>
                <div className="float-right mt-1">
                    <small>
                        <a className="member-link" href="/signup"><span className="cursor-pointer">Sign Up</span></a> or <a className="member-link" href="/resetPassword"><span className="pr-1 cursor-pointer">Reset Password</span></a>
                    </small>
                </div>
            </fieldset>
        </Form>
    );
}
