import useForm from '../lib/useForm';
import styled from 'styled-components';
import Form from './styles/Form';
import ItemStyles from './styles/ItemStyles';

const StyledUploadFileBtn = styled.div`
label{
  padding:5px 10px;
  background:#efefef;
  position:relative;
  color:#606060;
  border-radius:2px;
  text-align:center;
  cursor:pointer
}

.upload-btn-file {
    position: absolute;
    z-index: 1000;
    opacity: 0;
    cursor: pointer;
    background:#efefef;
    color:#606060;
    right: 0;
    top: 0;
    height: 100%;
    font-size: 24px;
    width: 100%;
    cursor:pointer
}
`;

export default function CreateProduct () {
    const { inputs, handleChange, clearForm, resetForm } = useForm({
        name: 'nice book',
        price: 3245,
        ageRange: '5-6',
        author: 'Jane'
    });
    // const [name, setName] = useState('Wes')

    return (
        <fieldset disabled={loading} aria-busy={loading}>
            <form>
                <label htmlFor="name">
                    Name
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        placeholder="Name"
                        value={inputs.name}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor="price">
                    Price
                    <input 
                        type="number" 
                        id="price" 
                        name="price" 
                        placeholder="Price"
                        value={inputs.price}
                        onChange={handleChange}
                    />
                </label>
                <button type="button" onClick={clearForm}>Clear Form</button>
                <button type="button" onClick={resetForm}>Reset Form</button>
            </form>
        </fieldset>
    );
}
