import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';
import useForm from '../lib/useForm';
import styled from 'styled-components';
import { ALL_PRODUCTS_QUERY } from './Products';
import Form from './styles/Form';
import DisplayError from './ErrorMessage';
import Router from 'next/router';

const SINGLE_PRODUCT_QUERY = gql`
    query SINGLE_PRODUCT_QUERY($id: ID!) {
        Product(where: { id: $id }) {
            id
            name
            description
            inStock
            ageRange
            author
            price   
            photo {
                id
                image {
                publicUrlTransformed
                }
            }
            photo2 {
                id
                image {
                publicUrlTransformed
                }
            }
            photo3 {
                id
                image {
                publicUrlTransformed
                }
            }
            photo4 {
                id
                image {
                publicUrlTransformed
                }
            }
        }
    }
`;

const CREATE_PRODUCT_MUTATION = gql`
    mutation CREATE_PRODUCT_MUTATION (
        $name: String!
        $ageRange: String!
        $inStock: String!
        $author: String!
        $price: Int!
        $image: Upload
        $image2: Upload
        $image3: Upload
        $image4: Upload
        $description: String!
    ){
        createProduct(data: {
            name: $name
            ageRange: $ageRange
            inStock: $inStock
            author: $author
            price: $price
            description: $description
            status: "AVAILABLE"
            photo: { 
                create: { image: $image, altText: $name } 
                image: { publicUrlTransformed }
            }
            photo2: { create: { image: $image2, altText: $name } }
            photo3: { create: { image: $image3, altText: $name } }
            photo4: { create: { image: $image4, altText: $name } }
        }) {
            id
            name
            description
            inStock
            ageRange
            author
            price
        }
    }
`;

const StyledUploadFileBtn = styled.div`
label{
  padding:5px 10px;
  background:#efefef;
  position:relative;
  color:#606060;
  border-radius:2px;
  text-align:center;
  cursor:pointer
}

.upload-btn-file {
    position: absolute;
    z-index: 1000;
    opacity: 0;
    cursor: pointer;
    background:#efefef;
    color:#606060;
    right: 0;
    top: 0;
    height: 100%;
    font-size: 24px;
    width: 100%;
    cursor:pointer
}
`;

export default function CreateProduct () {
    const { inputs, handleChange, clearForm, resetForm } = useForm({
        name: 'nice book',
        price: 3245,
        ageRange: '5-6',
        author: 'Jane',
        inStock: 'In Stock',
        description: 'Testing',
        image: '',
        image2: '',
        image3: '',
        image4: '',
    });
    const [createProduct, { loading, error, data }] = useMutation(
        CREATE_PRODUCT_MUTATION, 
        {
            variables: inputs,
            refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
        }
    );
    console.log(createProduct);

    console.log(inputs.image.name);

    return (
        <Form 
            onSubmit={async (e) => {
                e.preventDefault();
                //Submit the inputfield to the backend
                const res = await createProduct();
                clearForm();
                Router.push({
                    pathname: `/product/${res.data.createProduct.id}`,
                });
            }}
        >
            <DisplayError error={error} />
            <fieldset disabled={loading} aria-busy={loading}>
                <StyledUploadFileBtn>
                    <label htmlFor="image">
                    
                        {!inputs.image && (
                            <p>Upload Book Image 1 Here</p>
                        )}

                        {inputs.image && (
                            <p>{inputs.image.name}</p>
                        )}

                        <input 
                            type="file" 
                            id="image" 
                            name="image"
                            required
                            className="upload-btn-file"
                            onChange={handleChange}
                        />

                        {inputs?.photo?.image?.publicUrlTransformed && (
                            <img 
                                width="200"    
                                src={inputs?.photo?.image?.publicUrlTransformed} 
                                alt={inputs?.photo.name} 
                            />
                        )}
                    </label>
                </StyledUploadFileBtn>
                <StyledUploadFileBtn>
                    <label htmlFor="image2">

                        {!inputs.image2 && (
                            <p>Upload Book Image 2 Here</p>
                        )}

                        {inputs.image2 && (
                            <p>{inputs.image2.name}</p>
                        )}

                        <input 
                            type="file" 
                            id="image2" 
                            name="image2"
                            required
                            className="upload-btn-file"
                            onChange={handleChange}
                        />
                    </label>
                </StyledUploadFileBtn>
                <StyledUploadFileBtn>
                    <label htmlFor="image3">

                        {!inputs.image3 && (
                            <p>Upload Book Image 3 Here</p>
                        )}

                        {inputs.image3 && (
                            <p>{inputs.image3.name}</p>
                        )}

                        <input 
                            type="file" 
                            id="image3" 
                            name="image3"
                            required
                            className="upload-btn-file"
                            onChange={handleChange}
                        />
                    </label>
                </StyledUploadFileBtn>
                <StyledUploadFileBtn>
                    <label htmlFor="image">
                        
                        {!inputs.image4 && (
                            <p>Upload Book Image 4 Here</p>
                        )}

                        {inputs.image4 && (
                            <p>{inputs.image4.name}</p>
                        )}
                        
                        <input 
                            type="file" 
                            id="image4" 
                            name="image4"
                            required
                            className="upload-btn-file"
                            onChange={handleChange}
                        />
                    </label>
                </StyledUploadFileBtn>
                <label htmlFor="name">
                   Book Title
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        placeholder="Book Title"
                        required
                        value={inputs.name}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor="author">
                    Book Author
                    <input 
                        type="text" 
                        id="author" 
                        name="author" 
                        placeholder="Book Author"
                        required
                        value={inputs.author}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor="price">
                    Price
                    <input 
                        type="number" 
                        id="price" 
                        name="price" 
                        placeholder="Price - (e.g 499 = £4.99)"
                        required
                        value={inputs.price}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor="ageRange">
                    In Stock
                    <input 
                        type="text" 
                        id="inStock" 
                        name="inStock" 
                        placeholder="In Stock - (e.g Only 5 left)"
                        required
                        value={inputs.inStock}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor="ageRange">
                    Age Range
                    <input 
                        type="text" 
                        id="ageRange" 
                        name="ageRange" 
                        placeholder="Enter an Age Range"
                        required
                        value={inputs.ageRange}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor="description">
                    Description
                    <textarea 
                        id="description" 
                        name="description" 
                        rows="10"
                        placeholder="Enter A Description" 
                        required 
                        value={inputs.description}
                        onChange={handleChange}
                    />
                </label>
                <button type="submit">Submit</button>
                <button type="button" onClick={clearForm}>Clear Form</button>
                <button type="button" onClick={resetForm}>Reset Form</button>
            </fieldset>
        </Form>
    );
}
