    const me = useUser();
    if (!me) return <CartStyles open>Guest Cart</CartStyles>;
    console.log(me);
    return <CartStyles open>
        <header>
            {/* <CloseButton onClick={toggleCart} title="close"><FontAwesomeIcon icon={faTimes} /></CloseButton> */}
